import { Button, CloseButton, Heading, Input } from "@chakra-ui/react";
import { Message } from "./Message";
import { useState, useRef, useEffect } from "react";

export const Chat = ({ messages, chatRoom, closeChat, sendMessage }) => {
    const [message, setMessage] = useState("");
    const messageEndRef = useRef();

    useEffect(() => {
        messageEndRef.current.scrollIntoView();
    }, [messages]);

    const onSendMessage = () => {
        sendMessage(message);
        setMessage("");
    };

    return (
        <div className="telegram-chat bg-white rounded-2xl shadow-xl border border-gray-200 flex flex-col w-3/5 max-w-4xl h-[85vh]">
            {/* Заголовок */}
            <div className="telegram-header">
                <div className="telegram-header-info">
                    <Heading size="lg" className="telegram-header-name">{chatRoom}</Heading>
                </div>
                <CloseButton onClick={closeChat} />
            </div>

            {/* Повідомлення */}
            <div className="telegram-message-area">
                {messages.map((messageInfo, index) => (
                    <Message messageInfo={messageInfo} key={index} />
                ))}
                <span ref={messageEndRef} />
            </div>

            {/* Поле вводу */}
            <div className="telegram-input-area">
                <div className="telegram-input-wrapper">
                    <Input
                        type="text"
                        className="telegram-input"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="Введіть повідомлення"
                    />
                    <Button className="telegram-send-button" onClick={onSendMessage}>
                        Відправити
                    </Button>
                </div>
            </div>
        </div>
    );
};
