import { Button, Heading, Input, Text } from "@chakra-ui/react";
import { useState } from "react";
import React from "react";


type WaitingRoomProps = {
  joinChat: (userName: string, chatRoom: string) => void;
};

export const WaitingRoom: React.FC<WaitingRoomProps> = ({ joinChat }) => {
  const [userName, setUserName] = useState("");
  const [chatRoom, setChatRoom] = useState("");

  const onSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    joinChat(userName.trim(), chatRoom.trim());
  };

  return (
    <div className="app-bg min-h-screen flex items-center justify-center p-6">
      <div className="waiting-card w-full max-w-md">
        <Heading size="lg" mb={4}>Онлайн чат</Heading>

        <form onSubmit={onSubmit}>
          <div className="mb-4">
            <Text fontSize="sm" className="text-gray-600 font-medium mb-1 block">
              Ім’я користувача
            </Text>
            <Input
              type="text"
              value={userName}
              onChange={(e) => setUserName(e.target.value)}
              placeholder="Введіть ваше ім’я"
              className="bg-gray-50"
              required
            />
          </div>

          <div className="mb-6">
            <Text fontSize="sm" className="text-gray-600 font-medium mb-1 block">
              Назва чату
            </Text>
            <Input
              type="text"
              value={chatRoom}
              onChange={(e) => setChatRoom(e.target.value)}
              placeholder="Введіть назву чату"
              className="bg-gray-50"
              required
            />
          </div>

          <Button type="submit" className="w-full py-2" variant="telegram">
            Приєднатися до чату
          </Button>
        </form>
      </div>
    </div>
  );
};
export default WaitingRoom;
