import { Button, Heading, Input, Text } from "@chakra-ui/react";

export const WaitingRoom = () => {
    return (
        <form className="max-w-md w-full bg-white p-8 rounded shadow-lg">
            <Heading>Онлайн чат</Heading>
            <div className="mb-4">
                <Text fontSize={"sm"}>Ім'я користувача</Text>
                <Input name="userName" placeholder="Введіть ваше ім'я" />
            </div>
            <div className="mb-4">
                <Text fontSize={"sm"}>Назва чату</Text>
                <Input name="chatRoom" placeholder="Введіть назву чата" />
            </div>
            <Button type="submit" colorScheme="blue" >
                Приєднатися до чату
            </Button>
        </form>
    );
}
