export const WaitingRoom = ({ joinChat }) => {
  const [userName, setUserName] = useState("");
  const [chatRoom, setChatRoom] = useState("");

  const onSubmit = (e) => {
    e.preventDefault();
    joinChat(userName, chatRoom);
  };

  return (
    <div className="form-wrapper">
      <Heading className="chakra-heading">Онлайн чат</Heading>
      <form onSubmit={onSubmit}>
        <div className="mb-4">
          <Text className="chakra-text">Ім’я користувача</Text>
          <Input
            type="text"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
            placeholder="Введіть ваше ім’я"
            className="chakra-input"
            required
          />
        </div>
        <div className="mb-6">
          <Text className="chakra-text">Назва чату</Text>
          <Input
            type="text"
            value={chatRoom}
            onChange={(e) => setChatRoom(e.target.value)}
            placeholder="Введіть назву чату"
            className="chakra-input"
            required
          />
        </div>
        <Button type="submit" className="chakra-button">
          Приєднатися до чату
        </Button>
      </form>
    </div>
  );
};
