import { extendTheme } from "@chakra-ui/react";

const theme = extendTheme({
  colors: {
    telegram: {
      50: "#E6F6FF",
      100: "#CCEEFF",
      500: "#0088cc",
    },
  },
  components: {
    Button: {
      baseStyle: {
        rounded: "lg",
      },
      variants: {
        telegram: {
          bg: "telegram.500",
          color: "white",
          _hover: { bg: "#007ab8" },
        },
      },
    },
    Input: {
      defaultProps: {
        focusBorderColor: "telegram.500",
      },
    },
  },
});

export default theme;
